mapper.update:
UPDATE calendar SET title = #{title}, content = #{content}, start1 = #{start}, end1 = #{end}, allday = #{allDay}, bordercolor = #{borderColor}, backgroundcolor = #{backgroundColor}, textcolor = #{textColor}, process = #{process},
parent = #{parent},
status = #{status} WHERE id = #{id}

mapper.select:
SELECT count(*) from calendar
where manager = #{manager} or pm = #{manager}

and (parent = #{workcode} or workcode = #{workcode})
